member.remarks:
The following code shows a simple usage of two devices. Creates an instance of ,
adds handler, updates the devices lists by timer and handles incoming WM_INPUT message by
overloading WndProc method.

public partial class MyForm : Form
{
	enum MyDevices
	{
		None=0,
		SpaceMouse=1,
		SoftMouse3D=2,
	}

	const uint VendorID_Logitech=0x046d; // SpaceMouses, SpaceNavigators, etc.
	const uint VendorID_GGS=0x241f; // Softmouse 3D

	RawInputProcessingHelper<MyDevices> rawInputProcessingHelper=new RawInputProcessingHelper<MyDevices>();

	public MyForm()
	{
		InitializeComponent();

		// Add handler to processing helper
		rawInputProcessingHelper.AddHandler(MyDevices.SpaceMouse, (info) => { return info.dwType==RIM_TYPE.HID&&info.hid.usUsage==HID_USAGE_GENERIC_DESKTOP.MULTIAXIS_CONTROLLER&&info.hid.usUsagePage==HID_USAGE_PAGE.GENERIC_DESKTOP&&info.hid.dwVendorId==VendorID_Logitech; });
		rawInputProcessingHelper.AddHandler(MyDevices.SoftMouse3D, (info) => { return info.dwType==RIM_TYPE.HID&&info.hid.usUsage==HID_USAGE_GENERIC_DESKTOP.JOYSTICK&&info.hid.usUsagePage==HID_USAGE_PAGE.GENERIC_DESKTOP&&info.hid.dwVendorId==VendorID_GGS; });

		// Inital update of devices
		rawInputProcessingHelper.UpdateDevices();

		// Register the supported devices
		bool err;
		err=RawInput.RegisterRawInputDevice(RIDEV.NONE, HID_USAGE_PAGE.GENERIC_DESKTOP, HID_USAGE_GENERIC_DESKTOP.MULTIAXIS_CONTROLLER); // SpaceMouse
		err=RawInput.RegisterRawInputDevice(RIDEV.NONE, HID_USAGE_PAGE.GENERIC_DESKTOP, HID_USAGE_GENERIC_DESKTOP.JOYSTICK); // SoftMouse3D
	}

	[PermissionSet(SecurityAction.Demand, Name="FullTrust")]
	protected override void WndProc(ref Message m)
	{
		if(m.Msg==WM.INPUT)
		{
			uint size;
			switch(rawInputProcessingHelper.GetHandlerFromWmInput(m.LParam, out size))
			{
				case MyDevices.SpaceMouse: ProcessInputSpaceMouse(m, size); break;
				case MyDevices.SoftMouse3D: ProcessInputSoftMouse3D(m, size); break;
				default: break;
			}
		}

		base.WndProc(ref m);
	}

	// Use a timer to update the devices.
	private void timerUpdateDevices_Tick(object sender, EventArgs e)
	{
		rawInputProcessingHelper.UpdateDevices();
	}
}
